import React, { Fragment, useEffect } from "react";
import {Button, Container, Form, FormGroup,Input } from "reactstrap";

const AddCourse=()=>{
    useEffect(()=>{
        document.title="Add courses";
    },[]);
    return (
        <Fragment>
            <h1 className="text-center my-3">Fill Course Details</h1>
            <Form>
                <FormGroup>
                    <label for="courseId">Course Id</label>
                    <Input type="text"placeholder="Enter here" name="userId" id="userId"></Input>

                </FormGroup>
                <FormGroup>
                    <label for="title">Course Title</label>
                    <Input type="text"placeholder="Enter title here" name="title" id="title"></Input>
                </FormGroup>
                <FormGroup>
                    <label for="description">Course Description</label>
                    <Input type="textarea"placeholder="Enter Description here" name="description" id="description" style={{height:150}}></Input>
                </FormGroup>
                <Container className="text-center my-3">
                    <Button color="success">Add Course</Button>
                    <Button color="warning m-2">Clear</Button>
                </Container>
            </Form>
        </Fragment>
    )
}

export default AddCourse;
