import React, { useEffect, useState } from "react";
import { Button } from "reactstrap";
import Course from "./Course";
import base_url from "../api/bootapi";
import axios from "axios";
import { toast } from "react-toastify";
const Allcourse=()=>{
    useEffect(()=>{
        document.title="All courses";
    },[]);

    const getAllCoursesFromServer=()=>{
        axios.get(`${base_url}/courses`).then(
            (response)=>{
                //success
                console.log(response.data);
                toast.success("Course has been loaded",{
                    position:"bottom-center"
                });
                setCourses(response.data);
            },
            (error)=>{
                //error
                console.log(error);
                // toast.error("something went wrong",{
                //     position:"bottom-center"
                // });
                toast('ðŸ¦„ Wow so easy!', {
                    position: "top-right",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: false,
                    pauseOnHover: true,
                    draggable: false,
                    progress: undefined,
                    theme: "light",
                    });
            }
        )
    }
    useEffect(()=>{
        getAllCoursesFromServer();
    },[])
    const [courses,setCourses]=useState ([
        {title:"Java Course", description:"This is Java course"},
        {title:"Spring Course", description:"This is Spring course"},
        {title:"SpringBoot Course", description:"This is SpringBoot course"},
        {title:"ReactJS Course", description:"This is ReactJS course"}
    ])

    return (
        <div>
            <h1 >All Courses</h1>
            <p>List of courses are as follows</p>
            {
                courses.length>0? courses.map((item)=> <Course key={item.id} course={item}></Course>) : "No courses"
            }
        </div>
    )
}

export default Allcourse;
