import React, { useEffect, useState } from "react";
import { Button } from "reactstrap";
import Course from "./Course";
import base_url from "../api/bootapi";
import axios from ""
const Allcourse=()=>{
    useEffect(()=>{
        document.title="All courses";
    },[]);
    const [courses,setCourses]=useState ([
        {title:"Java Course", description:"This is Java course"},
        {title:"Spring Course", description:"This is Spring course"},
        {title:"SpringBoot Course", description:"This is SpringBoot course"},
        {title:"ReactJS Course", description:"This is ReactJS course"}
    ])

    return (
        <div>
            <h1 >All Courses</h1>
            <p>List of courses are as follows</p>
            {
                courses.length>0? courses.map((item)=> <Course course={item}></Course>) : "No courses"
            }
        </div>
    )
}

export default Allcourse;
